class ExampleView(APIView):
    """
    A view that can accept POST requests with JSON content.
    """
    parser_classes = (JSONParser,)

    def post(self, request, format=None):
        return Response({'received data': request.data})  Or, if you're using the  @api_view  decorator with function based views.  from rest_framework.decorators import api_view
